// Import our action types
import { SET_ALERT, REMOVE_ALERT } from '../actions/types';


// Using a Switch Statement Most common practice
export default (state = [], action) => {
  switch (action.type) {
    case SET_ALERT:
      return [...state, action.payload];

    default:
      return state;
  }
};
