// Import React Library and Fragment component
import React from 'react';

// Import PropTypes from prop-types library
import PropTypes from 'prop-types'

// Import the link component from the react-router-dom
import { Link } from 'react-router-dom';


const ProfileItem = ({ profile: { user: { _id, name, avatar }, status, company, location, skills } }) => {
  return (
    <div className='profile bg-light'>
      <img src={avatar} alt="" className="round-img"/>
      <div>
        <h2>{name}</h2>
        <p>{status} {company && <span> at { company } </span>}</p>
        <p className="my-1">{location && <span> { location } </span>}</p>
        <Link to={`/profile/${_id}`} className="btn btn-primary">  </Link>
      </div>
    </div>
  );
};

ProfileItem.propTypes = {
  profile: PropTypes.object.isRequired,
}

export default ProfileItem;
