// Import React library and the Fragment component
import React, { Fragment } from 'react';

// Import HOOKS functions from react library
import { useState, useEffect } from 'react';

// Import the link component from the react-router-dom
import { Link } from 'react-router-dom';



const Register = () => {
  const[formData, serFormData] = useState();

  return (
    <Fragment>
      <h1 className="large text-primary">Sign Up</h1>
      <p className="lead"><i className="fas fa-user"></i> Create Your Account</p>

      <form className="form" action="">
        <div className="form-group">
        <input type="text" placeholder="Name" name="name" required />
        </div>

        <div className="form-group">
        <input type="email" placeholder="Email Address" name="email" />
        <small className="form-text"> This site uses Gravatar so if you want a profile image, use a
        Gravatar email</small>

        </div>
        <div className="form-group">
          <input type="password" placeholder="Password" name="password" minLength="6" />
        </div>

        <div className="form-group">
          <input type="password" placeholder="Confirm Password" name="password2" minLength="6" />
        </div>

        <input type="submit" className="btn btn-primary" value="Register" />
      </form>

      <p className="my-1">
        Already have an account? <Link to='/login'>Sign In</Link>
      </p>
    </Fragment>
  )
}

export default Register;
